  var  postorder = [4, 5, 2, 6, 7, 3, 1];
  var constructFromPrePost = function (preorder, postorder) {
    if (preorder.length === 0) {
      return null;
    }
    if (preorder.length === 1) {
      return new TreeNode(preorder[0]);
