      "content": "# 递归实现

注意typeof instanceof hasOwnProperty的使用

function deepClone(obj = {}) {
    if (typeof obj !== "object" || obj == null) {
      return obj;
    }
    let result;
    if (obj instanceof Array) {
      result = [];
    } else {
      result = {};
    }
    for (let key in obj) {
      if (obj.hasOwnProperty(key)) {
        //obj.hasOwnProperty(key)? 保证key不是原型的属性
        //接下来递归
        result[key] = deepClone(obj[key]);
      }
    }
    return result
  }
