  const a = 100
  return function(){
    console.log('a :>> ', a);
  }
}
const fn= create()
const a = 200
fn() //100
// 函数作为参数
function print(fn){
  const a = 200
  fn()
}
const a = 100
function fn(){
  console.log(a)
}
print(fn) //100
//自由变量的查找，是在函数定义的位置向上级作用域查找，而不是在函数执行的位置



# 闭包的应用场景

# 1、一个缓存的插件（封装对象的私有属性和方法）

// 闭包隐藏数据，只提供 api
function createcache() {
    const num=100
    const data = {} // 闭包中的数据，被隐藏，不被外界访问
    return {
        num:num,
        set: function (key, val) {
            data[key] = val
        },
        get: function (key) {
            return data[key]
        }
    }
}

const c = createcache()
console.log(c.num)//num此时就作为c私有属性
c.set('a', 100) //set此时作为c的私有方法
console.log( c.get('a') )


