# 3、函数防抖和截流

//截流
const throttle = (fn, wait) => {
 let pretime = date.now()
 return function() {
  if(date.now() - pre > wait) {
   fn()
    pretime = date.now()
  }
 }
}

//防抖
var debounce = function(func, delay) {
     var timer = null
     return function() {
         var that = this;
         var args = arguments;

         if(timer) {
             cleartimeout(timer);
         }

         timer = settimeout(function() {
             func.apply(that, args);
         }, delay)
     }
}


