const Koa = require('koa');
const app = new Koa();

// logger

app.use(async (ctx, next) => {
  await next(); //1
  const rt = ctx.response.get('X-Response-Time');//7
  console.log(`${ctx.method} ${ctx.url} - ${rt}`);//8
});

