  const start = Date.now();//2
  await next();//3
  const ms = Date.now() - start;//5
  ctx.set('X-Response-Time', `${ms}ms`);//6
});

// response
app.use(async ctx => {
  ctx.body = 'Hello World';//4
});

app.listen(3000);


